mapper.select:
select distinct(ocd.client_id), ocd.client_name, ocd.group_id, ocd.resource_ids, ocd.authorized_grant_types, ocd.web_server_redirect_uri, ocd.authorities, ocd.access_token_validity, ocd.refresh_token_validity, ocd.additional_information, ocd.autoapprove, ocd.is_publish from oauth_client_details ocd left join sys_user_application sua on sua.client_id = ocd.client_id where sua.is_delete = 0 and ocd.is_delete = 0
and ocd.client_id = #{clientId}
and sua.userId = #{userId}

select distinct(ocd.client_id), ocd.group_id, ocd.client_name, ocd.authorized_grant_types, ocd.web_server_redirect_uri, ocd.icon, ocd.is_publish from oauth_client_details ocd left join sys_user_application sua on sua.client_id = ocd.client_id where ocd.is_delete = 0
and sua.user_id = #{p.userId}
and ocd.client_name like concat('%', #{p.clientName}, '%')
and ocd.is_publish = #{p.isPublish}
