mapper.select:
select distinct(ocd.client_id), ocd.group_id, ocd.client_name, ocd.authorized_grant_types, ocd.web_server_redirect_uri, ocd.icon, ocd.is_publish, sag.group_name from oauth_client_details ocd left join sys_user_application sua on sua.client_id = ocd.client_id left join sys_app_group sag on sag.id = ocd.group_id where ocd.is_delete = 0
and sua.user_id = #{p.userId}
and (ocd.client_name like concat('%', #{p.clientName}, '%') or sag.group_name like concat('%', #{p.clientName}, '%'))
and ocd.is_publish = #{p.isPublish}

select distinct(ocd.client_id), ocd.original_secret, ocd.group_id, ocd.client_name, ocd.authorized_grant_types, ocd.web_server_redirect_uri, ocd.icon, ocd.is_publish, ocd.create_time, ocd.access_token_validity, ocd.refresh_token_validity, ocd.is_publish, sag.group_name from oauth_client_details ocd left join sys_user_application sua on sua.client_id = ocd.client_id left join sys_app_group sag on sag.id = ocd.group_id where ocd.is_delete = 0
and sua.user_id = #{p.userId}
and (ocd.client_name like concat('%', #{p.clientName}, '%') or sag.group_name like concat('%', #{p.clientName}, '%'))
and ocd.is_publish = #{p.isPublish}
order by ocd.create_time asc

select ocd.client_id, ocd.client_name
from oauth_client_details ocd
where ocd.is_delete = 0 and group_id = #{groupId}
